Page._content:
console.log('custom.js 已加载');

// 全局拦截，但放行代码区、评论区输入框、带.allow-copy的元素（捕获和冒泡阶段都处理）
['contextmenu','copy','cut','paste'].forEach(function(eventName){
  // 捕获阶段
  document.addEventListener(eventName, function(e) {
    if (
      e.target.closest('.highlight, pre, code, .comments, .comment, .allow-copy, #tcomment textarea, #tcomment input, #tcomment .el-textarea__inner, #tcomment .tk-input, #tcomment .tk-textarea')
    ) return;
    e.preventDefault();
  }, true); // 捕获阶段

  // 冒泡阶段
  document.addEventListener(eventName, function(e) {
    if (
      e.target.closest('.highlight, pre, code, .comments, .comment, .allow-copy, #tcomment textarea, #tcomment input, #tcomment .el-textarea__inner, #tcomment .tk-input, #tcomment .tk-textarea')
    ) return;
    e.preventDefault();
  }, false); // 冒泡阶段
});

// 自动为 Twikoo 评论区输入框加 allow-copy 类，确保可以复制粘贴
function enableTwikooInputCopy() {
  var inputList = document.querySelectorAll(
    '#tcomment textarea, #tcomment input, #tcomment .el-textarea__inner, #tcomment .tk-input, #tcomment .tk-textarea'
  );
  console.log('找到评论区输入框数量:', inputList.length);
  inputList.forEach(function(el){
    el.classList.add('allow-copy');
    el.onpaste = null;
    el.oncopy = null;
    el.oncut = null;
    el.oncontextmenu = null;
    ['paste','copy','cut','contextmenu'].forEach(function(eventName){
      el.addEventListener(eventName, function(e){
        e.stopPropagation();
      }, true);
      el.addEventListener(eventName, function(e){
        e.stopPropagation();
      }, false);
    });
  });
}
// 页面加载后每 1 秒自动执行一次，持续 10 秒
for (let i = 0; i < 10; i++) {
  setTimeout(enableTwikooInputCopy, 1000 * (i + 1));
}
// 监听评论区 DOM 变化，动态加类
var tcommentEl = document.getElementById('tcomment');
if (tcommentEl) {
  var observer = new MutationObserver(enableTwikooInputCopy);
  observer.observe(tcommentEl, { childList: true, subtree: true });
}

// 增加：自动打印评论区实际渲染出的所有元素，帮助定位真实输入框结构
function printTcommentElements() {
  var tcommentEl = document.getElementById('tcomment');
  if (tcommentEl) {
    tcommentEl.querySelectorAll('*').forEach(function(el){
      console.log('评论区元素:', el.tagName, 'id=', el.id, 'class=', el.className);
    });
  } else {
    console.log('未找到 #tcomment 节点');
  }
}
for (let i = 0; i < 10; i++) {
  setTimeout(printTcommentElements, 2000 * (i + 1));
}

